mapper.select:
select

from POI_BUMapping
where 1 = 1

  and rule_id = #{ruleId,jdbcType=INTEGER}


  and bu_id = #{buId,jdbcType=INTEGER}


  and poi_id = #{poiId,jdbcType=INTEGER}

mapper.insert:
insert into POI_BUMapping (poi_id, bu_id,rule_id,add_time, add_user, update_time,update_user)
values

  (#{item.poiId}, #{item.buId},#{item.ruleId},
  #{item.addTime}, #{item.addUser}, #{item.updateTime}, #{item.updateUser}
  )

mapper.select:
select
from POI_BUMapping where poi_id in
#{item}

mapper.insert:
delete from POI_BUMapping
where poi_id in

  #{item}
